<span class="line"><span style="color:#ABB2BF;">  </span><span style="color:#E5C07B;">values</span><span style="color:#ABB2BF;">.</span><span style="color:#E5C07B;">push</span><span style="color:#ABB2BF;">.</span><span style="color:#61AFEF;">apply</span><span style="color:#ABB2BF;">(</span><span style="color:#E06C75;">values</span><span style="color:#ABB2BF;">, </span><span style="color:#E5C07B;">arguments</span><span style="color:#ABB2BF;">);</span></span>
